ocumentation)#define LPobjDescription	"Generate random numbers from a 1/f^2 (""Brownian"") distribution"	// The following sets up the 'mAxL' and 'vers' resources	// It relies on the values above for resource IDs and names, as	// well as concrete values for the 'vers'(1) resource.#include "Litter Globals.r"	//	// -----------------------------------------	//	// Other resource definitions 	//	#ifdef RC_INVOKED		#define LPlessEq		"<="	#else		#define LPlessEq		"²"	#endif	// Assistance strings#define LPAssistIn1			"Bang (Generate random number)"#define LPAssistIn2			"Int (NN factor)"#define LPAssistOut1		"Float (Random value, 0 " LPlessEq " x " LPlessEq " 1)"#ifdef RC_INVOKED					// Must be Windows RC Compiler	STRINGTABLE DISCARDABLE		BEGIN		lpStrIndexLastStandard + 1,		LPAssistIn1		lpStrIndexLastStandard + 2,		LPAssistIn2		lpStrIndexLastStandard + 3,		LPAssistOut1		END#else								// Must be Mac OS Resource Compiler	resource 'STR#' (LPobjID, LPobjName) {		{	/* array StringArray: 4 elements */			LPStdStrings,								// Standard Litter Strings						// Assist strings			LPAssistIn1, LPAssistIn2,					// Inlets			LPAssistOut1								// Outlets		}	};#endif